// src/features/cart/cartSlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  items: [], // Array to hold items added to the cart
  itemCount: 0, // Track the number of items in the cart
};

const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    addToCart: (state, action) => {
      state.items.push(action.payload); // Add item to cart
      state.itemCount += 1; // Increment item count
    },
    removeFromCart: (state, action) => {
      state.items = state.items.filter(item => item.post_id !== action.payload.post_id); // Remove item from cart
      state.itemCount -= 1; // Decrement item count
    },
    resetCart: (state) => {
      state.items = [];
      state.itemCount = 0; // Reset the cart and item count
    }
  },
});

// Export actions
export const { addToCart, removeFromCart, resetCart } = cartSlice.actions;

// Export the reducer
export default cartSlice.reducer;
