// src/features/cart/cartSlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  items: [], // Array to hold items added to the cart
  itemCount: 0,
};

const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    addToCart: (state, action) => {
      const existingItem = state.items.find(item => item.id === action.payload.id);
      
      if (existingItem) {
        existingItem.quantity += 1; // Increase quantity of existing item
      } else {
        state.items.push({ ...action.payload, quantity: 1 }); // Add new item with quantity 1
      }
      state.itemCount += 1; // Increment item count
      
    },
    removeFromCart: (state, action) => {
      const { id, forceZeroQuantity } = action.payload;  // Destructure id and forceZeroQuantity from the payload
      const item = state.items.find(item => item.id === id);
      
      if (item) {
        if (forceZeroQuantity) {
          // Directly set the quantity to zero when forceZeroQuantity is true
          item.quantity = 0;
          state.items = state.items.filter(i => i.id !== id); // Remove the item completely if quantity is set to zero
        } else {
          if (item.quantity > 1) {
            // Decrease the quantity if it’s greater than 1
            item.quantity -= 1;
          } else {
            // Remove the item if the quantity is 1
            state.items = state.items.filter(i => i.id !== id);
          }
        }
        state.itemCount -= 1; // Decrement item count
        if (state.itemCount < 0) {
          state.itemCount = 0; // Prevent negative item count
        }
      }
      console.log("inside remove from cart");
    },
    

    resetCart: (state) => {
      state.items = [];
      state.itemCount = 0; // Reset the cart and item count
    },
    // increaseQuantity: (state, action) => {
    //   const item = state.items.find(item => item.id === action.payload.id);
    //   console.log("indise slice",state.item);
    //   item.quantity += 1; // Increase item quantity

    // },
    decreaseQuantity: (state, action) => {
      const item = state.items.find(item => item.id === action.payload.id);
      console.log(item.main_topic);
      console.log(item.quantity);
      const idd = item.id;
      if (item.quantity === 1)
      {
        console.log("Removing item from cart");
        removeFromCart({idd});
      }
      item.quantity -= 1; // Decrease item quantity

    },
  },
});

// Export actions
export const { addToCart, removeFromCart, resetCart,increaseQuantity,decreaseQuantity } = cartSlice.actions;

// Export the reducer
export default cartSlice.reducer;
