import React, { useState, useEffect } from 'react';

export default function FullCartView() {
    const [posts, setPosts] = useState([]);
    const [cart, setCart] = useState([]); // State to track the cart items

    useEffect(() => {
        // Hardcoded clothing item descriptions
        const hardcodedPosts = [
            { post_id: 1, main_topic: 'T-Shirt', question_asked: 'What is the material of this T-shirt?' },
            { post_id: 2, main_topic: 'Jeans', question_asked: 'Are these jeans stretchable?' },
            { post_id: 3, main_topic: 'Jacket', question_asked: 'Is this jacket waterproof?' },
            { post_id: 4, main_topic: 'Sneakers', question_asked: 'What sizes are available for these sneakers?' },
            { post_id: 5, main_topic: 'Sweater', question_asked: 'Is this sweater machine washable?' },
        ];

        // Setting the hardcoded data to the posts state
        setPosts(hardcodedPosts);
    }, []);

    const handleAddToCart = (post) => {
        setCart([...cart, post]); // Adds the clicked item to the cart
        console.log('Item added to cart:', post);
    };

    return (
      <div>
        <h1>Latest Posts</h1>
        {posts.length > 0 ? (
          <ul>
            {posts.map((post) => (
              <li key={post.post_id}>
                <strong>Main Topic:</strong> {post.main_topic}, <strong>Question Asked:</strong> {post.question_asked}
                <button onClick={() => handleAddToCart(post)}>Add to Cart</button>
              </li>
            ))}
          </ul>
        ) : (
          <p>Loading posts...</p>
        )}
        
        {/* Cart Information */}
        {cart.length > 0 && (
          <div>
            <h2>Items in Cart</h2>
            <ul>
              {cart.map((cartItem, index) => (
                <li key={index}>{cartItem.main_topic}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    );
}



