import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { resetCart, removeFromCart,decreaseQuantity,increaseQuantity } from '../../store/slices/cartSlice'; // Import resetCart and removeFromCart actions
import '../../styles/Sidebar.css';

const Sidebar = ({ isOpen, toggleSidebar }) => {
  const cartItems = useSelector((state) => state.cart); // Access cart items from Redux store
  const dispatch = useDispatch(); // Initialize dispatch

  // Combine items with the same ID


  // Function to handle clearing the cart
  const handleClearCart = () => {
    dispatch(resetCart()); // Dispatch the resetCart action
  };

  // Function to handle removing an item from the cart
  const handleRemoveItem = (post_id) => {
    dispatch(removeFromCart({ post_id })); // Dispatch the removeFromCart action with post_id
  };
  const handleIncreaseQuantity = (post_id) => { 
    // console.log(post_id);
    dispatch(increaseQuantity({ post_id })); // Dispatch the increaseQuantity action with post_id
  };
  const handleDecreaseQuantity = (post_id) => { 
    dispatch(decreaseQuantity({ post_id })); // Dispatch the decreaseQuantity action with post_id
  };
  return (
    <div className={`sidebar ${isOpen ? 'open' : ''}`}>
      <h2>Cart Items</h2>
      {cartItems.itemCount > 0 ? (
        <ul>
          {cartItems.items.map((item, index) => (
            <li key={index}>
              {item.main_topic} - {item.quantity} x ${item.price}
              <button onClick ={() => handleIncreaseQuantity(item.post_id) }>+</button>
              <button onClick = { () => handleDecreaseQuantity(item.post_id) }>-</button>
              <button onClick={() => handleRemoveItem(item.post_id)}>Remove</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty.</p>
      )}

      <button className="close-sidebar">Checkout</button>
      <button className="close-sidebar" onClick={handleClearCart}>Clear Cart</button>
      <button className="close-sidebar" onClick={toggleSidebar}>Close</button>
    </div>
  );
};

export default Sidebar;
