import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { resetCart } from '../../store/slices/cartSlice'; // Import resetCart action
import '../../styles/Sidebar.css';

const Sidebar = ({ isOpen, toggleSidebar }) => {
  const cartItems = useSelector((state) => state.cart); // Access cart items from Redux store
  const dispatch = useDispatch(); // Initialize dispatch

  // Combine items with the same ID
  const combinedCartItems = cartItems.items.reduce((acc, item) => {
    const existingItem = acc.find((i) => i.post_id === item.post_id);
    if (existingItem) {
      existingItem.quantity += item.quantity;
    } else {
      acc.push({ ...item });
    }

    return acc;
  }, []);

  // Function to handle clearing the cart
  const handleClearCart = () => {
    dispatch(resetCart()); // Dispatch the resetCart action
  };

  return (
    <div className={`sidebar ${isOpen ? 'open' : ''}`}>
      <h2>Cart Items</h2>
      {combinedCartItems.length > 0 ? (
        <ul>
          {combinedCartItems.map((item, index) => (
            <li key={index}>
              {item.main_topic} - {item.quantity} x ${item.price} -<button onClick={() => dispatch(removeFromCart(item))}>Remove</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty.</p>
      )}

      <button className="close-sidebar">Checkout</button>
      <button className="close-sidebar" onClick={handleClearCart}>Clear Cart</button>
      <button className="close-sidebar" onClick={toggleSidebar}>Close</button>
    </div>
  );
};

export default Sidebar;
