import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { removeFromCart, addToCart, decreaseQuantity } from '../store/slices/cartSlice'; // Adjust the import paths accordingly

const FullCartView = () => {
  const dispatch = useDispatch();
  const cartItems = useSelector(state => state.cart.items); // Get cart items from Redux store

  const handleIncreaseQuantity = (postId) => {
    dispatch(addToCart({ post_id: postId }));
  };

  const handleDecreaseQuantity = (postId) => {
    dispatch(decreaseQuantity({ post_id: postId }));
  };

  const handleRemoveItem = (postId) => {
    dispatch(removeFromCart({ post_id: postId, forceZeroQuantity: false })); // Default behavior is to decrement quantity
  };

  if (cartItems.length === 0) {
    return (
      <div>
        <h1>Your Cart</h1>
        <p>Your cart is currently empty.</p>
      </div>
    );
  }

  return (
    <div>
      <h1>Your Cart</h1>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>
            <div>
              {item.main_topic} - {item.quantity} x ${item.price}
                <button onClick={() => handleIncreaseQuantity(item.post_id)}>+</button>
                <button onClick={() => handleDecreaseQuantity(item.post_id)}>-</button>
                <button onClick={() => handleRemoveItem(item.post_id)}>Remove</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FullCartView;
