import React, { useState } from 'react';

import { Routes, Route, Link } from 'react-router-dom';
import SignUp from './Signup';
import FetchUserData from './FetchUserData';
import SignIn from './SignIn';
import ProtectedPage from './ProtectedPage';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = () => {
    if (localStorage.getItem('Accesstoken'))
    {
      setIsAuthenticated(true);
    }
  }
  const handleLogout = () => {
    localStorage.removeItem('Accesstoken');
  }
  const checkAuth = async () => {
    const response = await fetch('http://localhost:8000/api/auth-check/', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('Accesstoken')}`,
      },
    });
    if (localStorage.getItem('Accesstoken')) {
      setIsAuthenticated(true);
    }
  }
  return (
    <div>
      <nav>
        <Link to="/">Home</Link> | <Link to="/create-accountss">Create Account</Link> | <Link to="/fetch-user-data">Fetch User Data</Link> | <Link to="/SignIn">Sign In</Link> | <Link to="/ProtectedPage">Protected Page</Link>
        <button onClick={handleLogout}>Logout</button>
      </nav>
      <Routes>
        <Route path="/" element={<h1>Welcome to the App</h1>} />
        <Route path="/fetch-user-data" element={<FetchUserData />} />
        <Route path="/create-accountss" element={<SignUp />} />
        <Route path="/SignIn" element={<SignIn />} />
        <Route 
          path="/ProtectedPage"
          element={
            <ProtectedPage isAuthenticated={isAuthenticated}>
              <h2>Protected Page</h2>
              <p>This page is protected and requires authentication to access.</p>
              <ProtectedPage />
            </ProtectedPage> 
          } />

      </Routes>
    </div>
  );
};
export default App;
