import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { resetCart, removeFromCart, decreaseQuantity, addToCart } from '../../store/slices/cartSlice'; // Import resetCart and removeFromCart actions
import '../../styles/Sidebar.css';

const Sidebar = ({ isOpen, toggleSidebar }) => {
  const cartItems = useSelector((state) => state.cart.items); // Access cart items from Redux store
  const dispatch = useDispatch(); // Initialize dispatch

  // Function to handle clearing the cart
  const handleClearCart = () => {
    dispatch(resetCart()); // Dispatch the resetCart action
  };

  // Function to handle removing an item from the cart
  const handleRemoveItem = (id) => {
    dispatch(removeFromCart({ id, forceZeroQuantity: true }));
  };

  const handleIncreaseQuantity = (id) => {
    dispatch(addToCart({ id })); // Dispatch the addToCart action with id
  };

  const handleDecreaseQuantity = (id) => {
    dispatch(decreaseQuantity({ id }));
  };

  const groupedItems = cartItems.reduce((acc, item) => {
    acc[item.id] = acc[item.id] || { ...item, quantity: 0 };
    acc[item.id].quantity += 1;
    return acc;
  }, {});

  const itemsArray = Object.values(groupedItems);

  return (
    <div className={`sidebar ${isOpen ? 'open' : ''}`}>
      <h2>Cart Items</h2>
      {itemsArray.length > 0 ? (
        <ul>
          {itemsArray.map((item, index) => (
            <li key={index}>
              {item.main_topic} - {item.quantity} x ${item.price}
              <button onClick={() => handleIncreaseQuantity(item.id)}>+</button>
              <button onClick={() => handleDecreaseQuantity(item.id)}>-</button>
              <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty.</p>
      )}

      <button className="close-sidebar">Checkout</button>
      <button className="close-sidebar" onClick={handleClearCart}>Clear Cart</button>
      <button className="close-sidebar" onClick={toggleSidebar}>Close</button>
    </div>
  );
};

export default Sidebar;
