// src/components/DisplayProduct.js
import React from 'react';
import '../styles/DisplayProduct.css';  // Import the CSS file for styling
import shirtImage from '../assets/shirt.png';  // Import the image

export default function DisplayProduct({ products, onAddToCart }) {
  return (
    <div className="posts-container">
      <h1>Products</h1>
      {products.length > 0 ? (
        <div className="posts-grid">
          {products.map((product) => (
            <div key={product.id} className="post-card">
              <img src={shirtImage} alt="Product" className="product-image" />
              <h3 className="post-title">{product.title}</h3>
              <p className="post-description">{product.description}</p>
              <p className="post-price">${product.price.toFixed(2)}</p>
              <button className="add-to-cart-btn" onClick={() => onAddToCart(product)}>Add to Cart</button>
            </div>
          ))}
        </div>
      ) : (
        <p>Loading products...</p>
      )}
    </div>
  );
}
