import React, { useState,useEffect } from 'react';


export default function Form  () {
    const [formData, setFormData] = useState({
        id:'',
        email: '',
        username: '',

    });
    const [response, setResponse] = useState(null); 
    const handleChange = (event) => {
        console.log(event.target);
        const {name, value} = event.target;
        setFormData((prevState) => ({
            ...prevState,
            [name]: value,
        }));



    }
    const fetchData = async () => {
        try {
            const response = await fetch(`http://localhost:8000/api/user/${formData.id}/`);
            const data = await response.json();
            setFormData((prevData) => ({
                ...prevData,            // Spread the previous formData
                email: data.email,      // Update email
                username: data.username // Update username
              }));
            console.log(data);

        }
        catch (error) {
            console.log(error);
        }
        

    }
    useEffect(() => {
        console.log('this is a new render');
        
        fetchData();
    }, [formData.id]);

    const handleSubmit = (event) => {
        event.preventDefault();
        // try{
        //     const response = fetch('http://localhost:8000/api/passwordReset/', {
        //         method: 'POST',
        //         headers: {
        //             'Content-Type': 'application/json',
        //         },
        //         body: JSON.stringify(formData),
        //     });
        //     // console.log("formData", body);
        //     console.log("response", response);
            
        //     if (true) {
        //         setResponse('Success');
        //         console.log('Success');
        //     } else {
        //         setResponse('Failed');
        //         console.log('Failed');
        //     }
        // }
        // catch (error) { 
        //     setResponse=('Failed');
        // }
        // const {email, name, age} = formData;
        // alert(`
        // ____Your Details____\n
        // Email: ${email}
        // Name: ${name}
        // Age: ${age}
        // `);
    }
    return (
        <form onSubmit={handleSubmit}>
            {Object.keys(formData).map((key) => (
                <div key={key}>
                    <label htmlFor={key}>{key.charAt(0).toUpperCase() + key.slice(1)}</label>
                    <input
                        name={key}
                        placeholder={key.charAt(0).toUpperCase() + key.slice(1)}
                        value={formData[key]}
                        onChange={handleChange}
                    />
                </div>
            ))}
            <div>
                <button type="submit">Create Account</button>
            </div>
        </form>
    )

}
