import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { removeFromCart, addToCart, decreaseQuantity } from '../store/slices/cartSlice'; // Adjust the import paths accordingly

const FullCartView = () => {
  const dispatch = useDispatch();
  const cartItems = useSelector(state => state.cart.items); // Get cart items from Redux store

  const handleIncreaseQuantity = (id) => {
    dispatch(addToCart({ id: id }));
  };

  const handleDecreaseQuantity = (id) => {
    dispatch(decreaseQuantity({ id: id }));
  };

  const handleRemoveItem = (id) => {
    dispatch(removeFromCart({ id: id, forceZeroQuantity: true })); // Remove item completely
  };

  const groupedItems = cartItems.reduce((acc, item) => {
    acc[item.id] = acc[item.id] || { ...item, quantity: 0 };
    acc[item.id].quantity += 1;
    return acc;
  }, {});

  const itemsArray = Object.values(groupedItems);

  if (itemsArray.length === 0) {
    return (
      <div>
        <h1>Your Cart</h1>
        <p>Your cart is currently empty.</p>
      </div>
    );
  }

  return (
    <div>
      <h1>Your Cart</h1>
      <ul>
        {itemsArray.map((item, index) => (
          <li key={index}>
            <div>
              {item.main_topic} - {item.quantity} x ${item.price}
              <button onClick={() => handleIncreaseQuantity(item.id)}>+</button>
              <button onClick={() => handleDecreaseQuantity(item.id)}>-</button>
              <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FullCartView;
