// src/components/DisplayPosts.js
import React from 'react';

export default function DisplayPosts({ posts, onAddToCart }) {
    return (
        <div>
            <h1>Latest Posts</h1>
            {posts.length > 0 ? (
                <ul>
                    {posts.map((post) => (
                        <li key={post.post_id}>
                            <strong>Main Topic:</strong> {post.main_topic}, <strong>Question Asked:</strong> {post.question_asked}
                            <button onClick={() => onAddToCart(post)}>Add to Cart</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Loading posts...</p>
            )}
        </div>
    );
}
