// src/FullCartView.js
import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart } from '../store/slices/cartSlice';
import DisplayPosts from '../commponents/DisplayProducts'; // Import DisplayPosts component

export default function FullCartView() {
    const [posts, setPosts] = useState([]);
    const cartItems = useSelector(state => state.cart.items); // Access cart items from Redux store
    const dispatch = useDispatch(); // Dispatch actions

    useEffect(() => {
        // Hardcoded clothing item descriptions
        const hardcodedPosts = [
            { post_id: 1, main_topic: 'T-Shirt', question_asked: 'What is the material of this T-shirt?', price: 15.99, quantity: 1 },
            { post_id: 2, main_topic: 'Jeans', question_asked: 'Are these jeans stretchable?', price: 29.99, quantity: 1 },
            { post_id: 3, main_topic: 'Jacket', question_asked: 'Is this jacket waterproof?', price: 49.99 , quantity: 1},
            { post_id: 4, main_topic: 'Sneakers', question_asked: 'What sizes are available for these sneakers?', price: 39.99 , quantity: 1},
            { post_id: 5, main_topic: 'Sweater', question_asked: 'Is this sweater machine washable?', price: 19.99, quantity: 1 },
        ];

        // Setting the hardcoded data to the posts state
        setPosts(hardcodedPosts);
    }, []);

    const handleAddToCart = (post) => {
        dispatch(addToCart(post)); // Dispatch the action to add item to the cart
    };

    return (
        <div>
            <DisplayPosts posts={posts} onAddToCart={handleAddToCart} />
            
            {/* Cart Information (if needed) */}
            {/* {cartItems.length > 0 && (
                <div>
                    <h2>Items in Cart</h2>
                    <ul>
                        {cartItems.map((cartItem, index) => (
                            <li key={index}>
                                {cartItem.main_topic}
                                <button onClick={() => handleRemoveFromCart(cartItem)}>Remove</button>
                            </li>
                        ))}
                    </ul>
                </div>
            )} */}
        </div>
    );
}
