// src/components/DisplayPosts.js
import React, { useEffect, useState } from 'react';
import '../styles/DisplayProduct.css';  // Import the CSS file for styling
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const axiosInstance = axios.create({
  baseURL: 'https://dummyjson.com/', // Base URL for the API
  headers: {
    'Content-Type': 'application/json',
  },
});

export default function DisplayPosts({ onAddToCart }) {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axiosInstance.get('/products')
      .then(response => {
        setPosts(response.data.products);
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });
  }, []);

  return (
    <div className="posts-container">
      <h1>Latest Posts</h1>
      {posts.length > 0 ? (
        <div className="posts-grid">
          {posts.map((post) => (
            <div 
              key={post.id} 
              className="post-card" 
              onClick={() => navigate(`/product/${post.id}`)}
            >
              <img src={post.thumbnail} alt="Product" className="product-image" />
              <h3 className="post-title">{post.title}</h3>
              <p className="post-description">{post.description}</p>
              <p className="post-price">${post.price.toFixed(2)}</p>
              <button 
                className="add-to-cart-btn" 
                onClick={(e) => {
                  e.stopPropagation();
                  onAddToCart(post);
                }}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      ) : (
        <p>Loading posts...</p>
      )}
    </div>
  );
}
